mapper.update:
UPDATE contents
SET price=#{price}, dc=#{dc}, saleprice=#{saleprice}, point=#{point}, salecnt=#{salecnt}
WHERE contentsno = #{contentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE nbno=#{nbno}
WHERE nbno=#{nbno} AND (UPPER(title) LIKE '%' || #{word} || '%' OR UPPER(content) LIKE '%' || #{word} || '%' OR UPPER(word) LIKE '%' || #{word} || '%')

SELECT contentsno, adminno, nbno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point, r FROM ( SELECT contentsno, adminno, nbno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point, rownum as r FROM ( SELECT contentsno, adminno, nbno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point FROM contents
WHERE nbno=#{nbno}
WHERE nbno=#{nbno} AND (UPPER(title) LIKE '%' || #{word} || '%' OR UPPER(content) LIKE '%' || #{word} || '%' OR UPPER(word) LIKE '%' || #{word} || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM contents
WHERE contentsno=#{contentsno} AND passwd=#{passwd}

mapper.update:
UPDATE contents
SET title=#{title}, content=#{content},  word=#{word}, 
      price=#{price}, dc=#{dc}, saleprice=#{saleprice}, point=#{point}, salecnt=#{salecnt}
WHERE contentsno = #{contentsno}

UPDATE contents
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE contentsno = #{contentsno}

mapper.select:
SELECT COUNT(*) as cnt
FROM contents
WHERE nbno=#{nbno}
