mapper.select:
SELECT COUNT(*) as cnt 
FROM notice
WHERE noticeno=#{noticeno}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}
WHERE noticeno = #{noticeno}

UPDATE notice
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE noticeno = #{noticeno}

UPDATE notice
SET notice_cnt = notice_cnt + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET replycnt = replycnt + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET replycnt = replycnt - 1
WHERE noticeno = #{noticeno}
